import React, { useEffect, useState } from "react";
import axios from "axios";
import "./CatList.css";

const CatList = () => {
  const [cats, setCats] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCat, setSelectedCat] = useState(null); // 상세 정보를 표시할 고양이 상태 추가

  useEffect(() => {
    axios
      .get("http://localhost:8000/cats")
      .then((response) => {
        setCats(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredCats = cats.filter((cat) =>
    cat.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const openModal = (cat) => {
    setSelectedCat(cat);
  };

  const closeModal = () => {
    setSelectedCat(null);
  };

  return (
    <div className="container">
      <h1>🐾 사지 말고, 입양하세요! 🐾</h1>
      <h3 className="subtitle">귀여운 고양이들의 매력에 푹 빠져보세요!</h3>
      <div className="search-bar">
        <input
          type="text"
          placeholder="고양이 이름 검색..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
      </div>
      <ul>
        {filteredCats.map((cat) => (
          <li key={cat.id} className="cat-item" onClick={() => openModal(cat)}>
            <div className="cat-info">
              <h2>{cat.name}</h2>
              <p>나이: {cat.age}</p>
              <p>품종: {cat.breed}</p>
            </div>
            <img src={cat.imageUrl} alt={cat.name} className="cat-image" />
          </li>
        ))}
      </ul>

      {/* 모달 */}
      {selectedCat && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <h2>{selectedCat.name}</h2>
            <p>나이: {selectedCat.age}</p>
            <p>품종: {selectedCat.breed}</p>
            <img
              src={selectedCat.imageUrl}
              alt={selectedCat.name}
              className="modal-image"
            />
            {/* 여기에 추가적인 상세 정보를 표시할 수 있습니다 */}
          </div>
        </div>
      )}
    </div>
  );
};

export default CatList;
