import React from "react";
import styled from "styled-components";

const MemberList = ({ part }: { part: string }) => {
  // 가정: part에 따라 멤버 목록을 필터링
  const members = getMembersForPart(part); // 데이터 가져오는 함수

  return (
    <ListWrapper>
      {members.map((member, index) => (
        <MemberItem key={index}>{member}</MemberItem>
      ))}
    </ListWrapper>
  );
};

// 데이터 가져오는 함수 (예시)
const getMembersForPart = (part: string) => {
  // 실제 데이터로 대체
  const allMembers = ["멤버1", "멤버2", "멤버3", "멤버4", "멤버5", "멤버6"];
  if (part === "") return allMembers; // '전체'일 때 모든 멤버
  return allMembers.filter((member) => member.includes(part)); // 필터링 예시
};

export default MemberList;

const ListWrapper = styled.div`
  display: flex;
  flex-wrap: wrap; /* 여러 줄로 표시되게 함 */
  justify-content: center; /* 중앙 정렬 */
  width: 100%;
`;

const MemberItem = styled.div`
  padding: 10px;
  margin: 5px;
  font-size: 16px;
  font-weight: 600;
  border-radius: 5px;
  background-color: #f0f0f0;
  border: 1px solid #ddd;
  width: 150px; /* 버튼과 같은 크기로 설정 */
  text-align: center;
`;
