import React from "react";
import { usePart } from "../context/PartContext";
import styled from "styled-components";

const Left = () => {
  const { setPart } = usePart();

  return (
    <Wrapper>
      <Button onClick={() => setPart("프론트엔드")}>프론트엔드</Button>
      <Button onClick={() => setPart("백엔드")}>백엔드</Button>
      <Button onClick={() => setPart("기획/디자인")}>기획/디자인</Button>
    </Wrapper>
  );
};

export default Left;

const Wrapper = styled.div`
  width: 300px;
  height: 100%;
  border-right: 1.5px solid #e6e6e6;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const Button = styled.button`
  display: block;
  padding: 15px 30px;
  font-size: 18px;
  font-weight: 600;
  border-radius: 30px;
  width: 100%;
  margin-bottom: 15px;
  background-color: #ffffff;
  color: #333;
  border: 2px solid #ee7521;
  cursor: pointer;
  transition: background-color 0.3s, color 0.3s;
  &:hover {
    background-color: #ee7521;
    color: #ffffff;
  }
`;
