import React from "react";
import styled from "styled-components";
import member from "../sookmut"; // 데이터 가져오기

const MemberList = ({ part }: { part: string }) => {
  // 데이터 필터링
  const members =
    part === "" ? member : member.filter((member) => member.type === part);

  // 아기사자와 어른사자 구분
  const babyLions = members.filter((member) => member.type === "아기사자");
  const adultLions = members.filter((member) => member.type === "어른사자");

  return (
    <ListWrapper>
      <GroupWrapper>
        <Title>아기사자</Title>
        {babyLions.map((member, index) => (
          <MemberItem key={index}>{member.name}</MemberItem>
        ))}
      </GroupWrapper>
      <GroupWrapper>
        <Title>어른사자</Title>
        {adultLions.map((member, index) => (
          <MemberItem key={index}>{member.name}</MemberItem>
        ))}
      </GroupWrapper>
    </ListWrapper>
  );
};

export default MemberList;

const ListWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  width: 100%;
`;

const GroupWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 10px;
  flex: 1;
`;

const MemberItem = styled.div`
  padding: 10px;
  margin: 5px;
  font-size: 16px;
  font-weight: 600;
  border-radius: 5px;
  background-color: #f0f0f0;
  border: 1px solid #ddd;
  width: 150px;
  text-align: center;
`;

const Title = styled.h3`
  margin-bottom: 10px;
  font-size: 18px;
  font-weight: 700;
  color: #333;
`;
