// MemberList.js
import React from "react";
import styled from "styled-components";
import member from "../data/sookmut"; // Í≤ΩÎ°ú ÏàòÏ†ï

const MemberList = ({ part }) => {
  const memberlist = part ? member.filter((m) => m.part === part) : member;

  return (
    <List>
      {memberlist.map((mem) => (
        <Item key={mem.name}>
          <Name>ü¶Å {mem.name}</Name>
          <Role className={mem.role === "ÏïÑÍ∏∞ÏÇ¨Ïûê" ? "baby" : "adult"}>
            {mem.role}
          </Role>
        </Item>
      ))}
    </List>
  );
};

export default MemberList;

const List = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
  padding: 20px;
`;

const Item = styled.div`
  display: flex;
  align-items: center;
  font-size: 18px;
  font-weight: 500;
  gap: 10px;
`;

const Name = styled.div`
  font-size: 18px;
  font-weight: 500;
`;

const Role = styled.div`
  font-size: 14px;
  font-weight: 700;
  padding: 8px 12px;
  border-radius: 20px;
  text-align: center;
  &.baby {
    background-color: #ee7521;
    color: white;
  }
  &.adult {
    background-color: #ffe5c5;
    color: #333;
  }
`;
