import React from "react";
import styled from "styled-components";
import { usePart } from "../context/PartContext";

const Left = () => {
  const { part, setPart } = usePart();
  return (
    <Wrapper>
      <button onClick={() => setPart("프론트엔드")}>프론트엔드</button>
      <button onClick={() => setPart("백엔드")}>백엔드</button>
      <button onClick={() => setPart("기획/디자인")}>기획/디자인</button>
    </Wrapper>
  );
};

export default Left;

const Wrapper = styled.div`
  width: 300px;
  height: 100%;
  border-right: 1.5px solid #e6e6e6;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  button {
    display: block;
    padding: 15px 25px;
    font-size: 18px;
    font-weight: 600;
    border-radius: 50px;
    width: 150px;
    margin-bottom: 20px;
    background-color: white;
    border: 2px solid #ee7521;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.3s ease,
      box-shadow 0.3s ease;

    &:hover {
      background-color: #f9f9f9;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
      transform: scale(1.05);
    }
  }
`;
